опиши код кратко :private void button2_Click(object sender, EventArgs e)
        {
            count = 0;
            string A;
            s1 = (int)servic1.SelectedValue;
            string query = "SELECT SUM(Price) from services Where Code ='" + s1 + "'or Code = '" + s2 + "' or Code ='" + s3 + "'";
