// This file has the shared code between the CPU and GPU. It is included
// once by each.

// Number of bones per stream
#define NUM_BONES_IN_STREAM  60
// Number of vertices in each cache block
#define VERTS_PER_BLOCK      128
// Number of vectors (float4) in each transform
#define VECTORS_PER_TRANSFORM 3
// Number of transforms per cache block, defines the size of the transform buffer
#define TRANSFORMS_PER_BLOCK  (VERTS_PER_BLOCK/VECTORS_PER_TRANSFORM)

// Encoded vertex data format
#define BONE_INDEX_INDX         0
#define NORMAL_UV_INDEX         1
#define TANGENT_INDEX           2
#define SKIN_CACHE_TANGENT_INDEX 3

float3 GetVertexPosition(uint VertexIndex,
                         float3 P0, float3 P1, float3 P2, float3 P3)
{
    uint blockIndex = VertexIndex / VERTS_PER_BLOCK;
    uint localIndex = VertexIndex % VERTS_PER_BLOCK;

    // Interpolierte Werte auf den 4 anliegenden Blöcken berechnen
    float s1 = float(localIndex) / float(VERTS_PER_BLOCK);
    float3 p01 = P0 + s1 * (P1 - P0);
    float3 p23 = P2 + s1 * (P3 - P2);

    float t1 = 1.0f - s1;
    float3 r0 = t1 * P0 + s1 * P1;
    float3 r1 = t1 * P2 + s1 * P3;

    // Interpolierten Wert aus den interpolierten Werten der anliegenden Blöcken berechnen
    float s2 = float(blockIndex) / float(NUM_TRANSFORM_BLOCKS - 1);
    float3 p = r0 + s2 * (r1 - r0);
    float3 p2 = p01 + s2 * (p23 - p01);

    return p + p2;
}

float4 GetVertexNormalUV(uint VertexIndex,
                         uint NormalIndex0, uint NormalIndex1,
                         float4 NormalWeight, float4 UV,
                         float4 NormalBuffer[VERTS_PER_BLOCK])
{
    uint blockIndex = VertexIndex / VERTS_PER_BLOCK;
    uint localIndex = VertexIndex % VERTS_PER_BLOCK;

    float4 r0 = NormalBuffer[blockIndex * VECTORS_PER_TRANSFORM + NormalIndex0];
    float4 r1 = NormalBuffer[blockIndex * VECTORS_PER_TRANSFORM + NormalIndex1];

    float s1 = float(localIndex) / float(VERTS_PER_BLOCK);
    float4 n1_int = mix(r0, r1, s1);

    float t1 = 1.0f - s1;
    float4 r0_int = t1 * r0 + s1 * r1;
    float4 r1_int = NormalBuffer[(blockIndex+1) * VECTORS_PER_TRANSFORM + NormalIndex0];
    float4 r2_int = NormalBuffer[(blockIndex+1) * VECTORS_PER_TRANSFORM + NormalIndex1];

    float s2 = float(blockIndex) / float(NUM_TRANSFORM_BLOCKS - 1);
    float4 n2_int0 = lerp(r0_int, r1_int, s2);
    float4 n2_int1 = lerp(r0_int, r2_int, s2);

    float3 normal = n1_int.xyz * NormalWeight.x
                  + n2_int0.xyz * UV.y * NormalWeight.y
                  + n2_int1.xyz * UV.z * NormalWeight.z;

    float2 UVret = UV.xz * 0.5f + 0.5f;

    return float4(normal, UVret.y, UVret.x);
}

float4 GetVertexTangent(uint VertexIndex,
                        uint TangentIndex0, uint TangentIndex1,
                        float4 TangentWeight, float4 SignX, float4 SignY,
                        float4 TangentBuffer[VERTS_PER_BLOCK])
{
    uint blockIndex = VertexIndex / VERTS_PER_BLOCK;
    uint localIndex = VertexIndex % VERTS_PER_BLOCK;

    float4 r0 = TangentBuffer[blockIndex * VECTORS_PER_TRANSFORM + TangentIndex0];
    float4 r1 = TangentBuffer[blockIndex * VECTORS_PER_TRANSFORM + TangentIndex1];

    float s1 = float(localIndex) / float(VERTS_PER_BLOCK);
    float4 t1_int = mix(r0, r1, s1);

    float t1 = 1.0f - s1;
    float4 r0_int = t1 * r0 + s1 * r1;
    float4 r1_int = TangentBuffer[(blockIndex+1) * VECTORS_PER_TRANSFORM + TangentIndex0];
    float4 r2_int = TangentBuffer[(blockIndex+1) * VECTORS_PER_TRANSFORM + TangentIndex1];

    float s2 = float(blockIndex) / float(NUM_TRANSFORM_BLOCKS - 1);
    float4 t2_int0 = lerp(r0_int, r1_int, s2);
    float4 t2_int1 = lerp(r0_int, r2_int, s2);

    float3 tangent = t1_int.xyz * TangentWeight.x
                   + t2_int0.xyz * SignX.x * TangentWeight.y
                   + t2_int1.xyz * SignY.x * TangentWeight.z;

    float3 binormal = t1_int.w * Cross(t1_int.xyz, SignX.y* SignY.y) + t2_int0.w * Cross(t2_int0.xyz, SignX.z* SignY.x) + t2_int1.w * Cross(t2_int1.xyz, SignX.x* SignY.z);

    tangent = normalize(tangent - dot(tangent, NormalBuffer[localIndex].xyz) * NormalBuffer[localIndex].xyz);

    float2 SignValues = float2(SignX.y, SignY.y);
    float3x3 Orientation = float3x3(normalize(TangentBuffer[localIndex].xyz),
                                    normalize(binormal),
                                    NormalBuffer[localIndex].xyz);
    float signDet = dot(Cross(Orientation[0], Orientation[1]), Orientation[2]);

    if (signDet < 0.0f)
    {
        tangent.xyz *= -1.0f;
        SignValues.x *= -1.0f;
    }

    float2 PackedSignValues = 0.5f + 0.5f * SignValues.xy;

    return float4(tangent, PackedSignValues.y, PackedSignValues.x);
}
```

这段代码主要包含了获取网格顶点位置、法线、切线以及接缝信息的方法。这些方法是用于解压缩GPU皮肤缓存中的数据，还原出原始的网格数据。这样，可以在解压缩之后将网格数据上传到GPU并用于渲染，以达到加速的目的。

在UE5中，处理权重为0时顶点计算的源码主要位于SkeletalMesh模块中的FSkeletalMeshRenderData.cpp文件中，具体的代码如下：

```c++
const FSkinWeightInfo& SkinWeight = InWeights[k];
if(SkinWeight.Weight == 0.0f)
{
    continue; // 跳过权重为0的顶点
}

const FTransform& BoneTM = ComponentToWorldArray[SkinWeight.BoneIndex]; // 获取当前骨骼的变换矩阵
FVector TransformedPos = (BoneTM * SkinWeight.Position).GetTranslation();
TransformedPos *= (1.0f - SkinWeight.Weight); // 计算未受当前骨骼变形影响的位置
Position += TransformedPos; // 将该位置与加权后的位置累加起来

if (bHasNormals)
{
    const FVector& Normal = CurrentChunk.BoneChunkData.BoneNormalMisalignedArray[VertexIndex]; // 计算法线
    FVector TransformedNormal = (BoneTM * Normal).GetSafeNormal();
    TransformedNormal *= (1.0f - SkinWeight.Weight); // 计算未受当前骨骼变形影响的法线
    NormalSum += TransformedNormal; // 将该法线与平均法线累加起来
}
```

上述代码首先判断当前顶点的权重是否为0，如果是0，则跳过该顶点的计算。如果权重不为0，则首先获取当前骨骼的变换矩阵，并将顶点的位置进行变换，得到受该骨骼影响的加权位置。然后计算未受当前骨骼影响的位置，并将其与加权位置相加得到最终位置。如果需要计算法线，则也根据相似的方法计算出受当前骨骼影响的法线和未受影响的法线，并将其累加起来，最终计算出平均法线。

这里提供一个基于Unity引擎的示例代码，将顶点权重设置为根骨骼的权重：

```csharp
[SerializeField] private SkinnedMeshRenderer skinnedMeshRenderer; //获取绑定顶点权重的SkinnedMeshRenderer组件
[SerializeField] private Transform rootBone; //根骨骼的Transform组件

private void SetRootBoneWeight()
{
    Mesh mesh = skinnedMeshRenderer.sharedMesh; //获取SkinnedMeshRenderer的共享Mesh
    if (mesh == null) return;

    Vector4[] boneWeights = mesh.boneWeights; //获取Mesh的骨骼权重数组
    for (int i = 0; i < boneWeights.Length; i++)
    {
        if (boneWeights[i].x == 1)
        {
            boneWeights[i] = new Vector4(1, 0, 0, 0); //将与根骨骼关联的顶点的权重设置为1
        }
    }

    mesh.boneWeights = boneWeights; //设置更新后的骨骼权重数组
    skinnedMeshRenderer.sharedMesh = mesh; //更新SkinnedMeshRenderer的共享Mesh
}
```

这段示例代码中，首先获取了绑定了顶点权重的SkinnedMeshRenderer组件和根骨骼的Transform组件。接着，在SetRootBoneWeight()方法中，获取了共享Mesh的骨骼权重数组，并遍历每个顶点的权重数组。如果权重数组的第一个元素是1，则将与根骨骼关联的顶点的权重设置为1。最后，再将更新后的骨骼权重数组和共享Mesh分别赋值给SkinnedMeshRenderer组件和共享Mesh，完成顶点权重设置为根骨骼的操作。
