using System;
using System.Threading;

namespace RocketAcceleration
{
    class Program
    {
        static void Main(string[] args)
        {
            // Задаем начальные условия ракеты
            float startSpeed = 0;
            float acceleration = 10; // ускорение в метрах в секунду
            int totalTime = 0;

            Console.WriteLine("Ракета готова к запуску.");
            Console.WriteLine("Для запуска ракеты нажмите Enter...");
            Console.ReadLine();

            while (true)
            {
                float currentSpeed = startSpeed + acceleration * totalTime;

                Console.Clear();
                Console.WriteLine($"Прошло времени: {totalTime} секунд");
                Console.WriteLine($"Текущая скорость ракеты: {currentSpeed} м/с");

                totalTime++;
                Thread.Sleep(1000);
            }
        }
    }
}

