#include <iostream>

int countFemalesOverHeight(char sexe[], float taille[], int n, float height) {
  int count = 0;
  for (int i = 0; i < n; i++) {
    if (sexe[i] == 'F' && taille[i] > height) {
      count++;
    }
  }
  return count;
}

int countMalesOverWeight(char sexe[], float poids[], int n, float weight) {
  int count = 0;
  for (int i = 0; i < n; i++) {
    if (sexe[i] == 'M' && poids[i] > weight) {
      count++;
    }
  }
  return count;
}

int main() {
  int numero[] = {2010, 1632, 4319};
  char sexe[] = {'F', 'M', 'F'};
  float taille[] = {1.75, 1.68, 1.89}, poids[] = {65.3, 78.4, 67.1};
  int nbPers = sizeof(numero) / sizeof(int);

  int femaleCount = countFemalesOverHeight(sexe, taille, nbPers, 1.82);
  int maleCount = countMalesOverWeight(sexe, poids, nbPers, 98.7);

