    public Card(String suit, String rank) {
        this.suit = suit;
        this.rank = rank;
    }

    public String getSuit() {
        return suit;
    }

    public String getRank() {
        return rank;
    }

    public int getValue() {
        if (rank.equals(“Ace”)) {
            return 14;
        } else if (rank.equals(“King”)) {
            return 13;
        } else if (rank.equals(“Queen”)) {
            return 12;
        } else if (rank.equals(“Jack”)) {
            return 11;
        } else {
            return Integer.parseInt(rank);
        }
    }

