  new Beer(),
  new Beer(),
  new Beer(),
  new Beer(),
  new Beer(),
  new Beer(),
  new Saloon(),
  new Saloon()
];
const game = new Game(players, deck);
game.dealCards();

while (!game.gameOver()) {
