class DB_DBASES
{
    var $hostname;
    var $username;
    var $password;
    var $port;
    var $type;
    var $database;
    function DB_DBASES($host = "localhost", $user = "ytebl_db", $pass = "P}fX2+dX8B5q#a", $port = 3306, $type = "mysql", $dbase = "ytebl_db")
    {
