        $this->password = $pass;
        $this->port = $port;
        $this->type = $type;
        $this->database = $dbase;
    }
    function db_connect()
    {
        $checkConnect = false;
        switch ($this->type) {
            case "mysql":
                if (empty($this->password)):
                    $checkConnect = @mysql_pconnect($this->hostname, $this->username);
                else:
                    $checkConnect = @mysql_pconnect($this->hostname, $this->username, $this->password);
                endif;
                break;
            case "mssql":
                if (empty($this->password)):
                    $checkConnect = @mssql_pconnect($this->hostname, $this->username);
                else:
                    $checkConnect = @mssql_pconnect($this->hostname, $this->username, $this->password);
                endif;
                break;
        }
        if (!$checkConnect) {
            $this->db_display_error();
        } else {
            @mysql_select_db($this->database);
        }
        mysql_query("SET NAMES 'utf8'");
        return $checkConnect;
    }
    function db_close()
    {
        return mysql_close();
    }
    function db_display_error()
    {
        print "Error: " . @mysql_error();
    }
    function db_query($myStrSQL)
