        switch ($this->type) {
            case "mysql":
                $result = @mysql_query($myStrSQL);
                break;
            case "mssql":
                $result = @mssql_query($myStrSQL);
                break;
        }
        if (!$result) {
            $this->db_display_error();
        }
        return $result;
    }
    function db_fetch_array($result)
    {
