    {
        $this->hostname = $host;
        $this->username = $user;
        $this->password = $pass;
        $this->port = $port;
        $this->database = $dbase;
    }

    function db_connect()
    {
        $this->connection = mysqli_connect($this->hostname, $this->username, $this->password, $this->database, $this->port);

        if (!$this->connection) {
            $this->db_display_error();
            return false;
        }

        mysqli_set_charset($this->connection, "utf8");
        return true;
    }

    function db_close()
    {
        return mysqli_close($this->connection);
    }

    function db_display_error()
    {
        print "Error: " . mysqli_error($this->connection);
    }

    function db_query($myStrSQL)
    {
        $result = mysqli_query($this->connection, $myStrSQL);

        if (!$result) {
            $this->db_display_error();
        }
        return $result;
    }

    function db_fetch_array($result)
    {
        return mysqli_fetch_array($result);
    }

    function db_num_rows($result)
    {
        return mysqli_num_rows($result);
    }

    function db_insert_id()
    {
        return mysqli_insert_id($this->connection);
    }
}
