def download_from_best_mirror(beatmapset_id: int):
    for url in osu_mirrors:
        success, downloaded_beatmapset_id, file_name = download_beatmapset(beatmapset_id, url)
        if success:
            return (downloaded_beatmapset_id, file_name)
    return (beatmapset_id, "")


def main(file_name: str):
    with open(file_name, "r") as f:
        beatmapset_ids = [int(line.strip()) for line in f]

    output_dir = file_name.replace(".txt", "")
    os.makedirs(output_dir, exist_ok=True)
    os.chdir(output_dir)

    with ThreadPoolExecutor(max_workers=5) as executor:
        results = {executor.submit(download_from_best_mirror, beatmapset_id): beatmapset_id for beatmapset_id in beatmapset_ids}
        all_downloaded_files = []

        for future in as_completed(results):
            beatmapset_id = results[future]
            downloaded_beatmapset_id, file_name = future.result()
