            if (!string.IsNullOrEmpty(routeStr))
            {
                app.UseRouter(routes =>
                {
                    routes.MapMiddlewareRoute(routeStr, appBuilder =>
                    {
                        appBuilder.UseMessageHandlerMiddleware(); //微信消息集成处理
