        string streamUrl2 = "http://192.168.0.1:8080/stream2";
        string ffmpegPath = @"C:\ffmpeg\bin\ffmpeg.exe";
        string outputFolder = @"C:\videos\";

        var tasks = new ConcurrentQueue<Task>();
        tasks.Enqueue(new Task(streamUrl1, Path.Combine(outputFolder, "video1.mp4"), ffmpegPath));
        tasks.Enqueue(new Task(streamUrl2, Path.Combine(outputFolder, "video2.mp4"), ffmpegPath));

        var threads = new List<Thread>();
        for (int i = 0; i < tasks.Count; i++)
        {
            var thread = new Thread(() =>
            {
                while (!tasks.IsEmpty)
                {
                    if (tasks.TryDequeue(out var task))
                    {
                        try
                        {
                            Console.WriteLine($"Starting task: {task.InputUrl}");
                            task.Run();
                            Console.WriteLine($"Completed task: {task.InputUrl}");
                        }
                        catch (Exception ex)
                        {
                            Console.WriteLine($"Error in task: {task.InputUrl}. Error message: {ex.Message}");
                        }
                    }
                }
            });
            threads.Add(thread);
            thread.Start();
        }

        foreach (var thread in threads)
        {
            thread.Join();
        }

        Console.WriteLine("All done.");
        Console.ReadKey();
    }

