def generate_quarter_ranges(date_range):
    start_date, end_date = date_range

    def get_next_quarter_start(date):
        if date.month < 4:
            return date.replace(month=4, day=1)
        elif date.month < 7:
            return date.replace(month=7, day=1)
        elif date.month < 10:
            return date.replace(month=10, day=1)
