function get_data_by_10days($start_date, $end_date) {
  $date_array = array();
  $start_timestamp = strtotime($start_date);
  $end_timestamp = strtotime($end_date);
  $interval = 86400 * 10; // 10 days interval in seconds

  while ($start_timestamp < $end_timestamp) {
    $next_timestamp = $start_timestamp + $interval;
    if ($next_timestamp > $end_timestamp) {
      $next_timestamp = $end_timestamp;
    }
    $date_array[] = array(date('Y-m-d', $start_timestamp), date('Y-m-d', $next_timestamp));

    $start_timestamp = $next_timestamp + 1; // add 1 second to exclude the current end time from next start time
  }

  return $date_array;
}
