                    break;
                default:
                    terminal.innerHTML += "<p>'" + command + "' is not recognized as an internal or external command, operable program or batch file.</p>";
            }
        }

        // 监听用户输入
        function handleInput(event) {
            if (event.keyCode == 13) { // 回车键
                event.preventDefault();
                var input = event.target.value.trim();
                if (input.length > 0) {
                    terminal.lastChild.lastChild.nodeValue += input;
                    handleCommand(input);
                    addPrompt();
                    event.target.value = "";
                }
            }
        }

        // 初始化终端
        function init() {
            addPrompt();
            terminal.addEventListener("keydown", handleInput);
            terminal.focus();
        }

        init();
    </script>
</body>
</html>
```

