using namespace DirectX;

int main() {
    XMVECTOR v1 = XMVectorSet(1.0f, 2.0f, 3.0f, 0.0f);
    XMVECTOR v2 = XMVectorSet(-4.0f, 5.0f, -6.0f, 0.0f);
