app.get('/api/game', (req, res) => {
  res.json(gameBoard);
});

app.post('/api/game', (req, res) => {
  let { row, column } = req.body;
  let index = row * 3 + column;
  if (gameBoard[index] === '_') {
    gameBoard[index] = player;
    player = player === 'X' ? 'O' : 'X';
    res.sendStatus(200);
  } else {
    res.sendStatus(400);
  }
});

app.listen(port, () => {
