Спасибо за вопрос! Предыдущее решение работает, но можно улучшить его читаемость и сделать его более элегантным. Вот решение, написанное в соответствии с принципами чистого кода и лучших практик:

```javascript
function calculateString(inputStr) {
  let stack = [];
  let currentNum = '';
  let currentOp = '+';

  for (let i = 0; i < inputStr.length; i++) {
    const char = inputStr[i];
    const isDigit = !isNaN(char);

    if (isDigit) {
      currentNum += char;
      continue;
    }

    if (currentNum) {
      stack.push(currentOp === '*' ? 3 * parseInt(currentNum) : parseInt(currentNum));
      currentNum = '';
    }

    if (char === ')') {
      let num = stack.pop();
      while (stack[stack.length - 1] !== '(') {
        num = executeOperation(stack.pop(), num, stack.pop());
      }
      stack.pop();
      currentOp = '*';
      stack.push(num);
    } else {
      currentOp = char;
      stack.push(char);
    }
  }

  if (currentNum) {
    stack.push(currentOp === '*' ? 3 * parseInt(currentNum) : parseInt(currentNum));
  }

  let result = stack.shift();
  while (stack.length) {
    result = executeOperation(result, stack.shift(), stack.shift());
  }

  return result;
}

function executeOperation(left, right, op) {
  switch (op) {
    case '+':
      return left + right;
    case '-':
      return left - right;
  }
}

