const RingProgress = ({ percent, strokeWidth, radius }) => {
  const [offset, setOffset] = useState(0);
  const [color, setColor] = useState("#3F3D56");

  const effectiveStrokeWidth = strokeWidth || 10;
  const R = radius || 50;
  const C = 2 * Math.PI * R;
  const value = percent || 0;
  const size = R * 2 + effectiveStrokeWidth;

  useEffect(() => {
    const progress = (100 - value) / 100 * C;
    setOffset(progress);
    setColor("#FDBB06");
  }, [setOffset, setColor, value, C, R]);

  return (
    <div
      style={{
        width: size,
        height: size,
        position: "relative",
      }}
    >
      <svg
        width={size}
        height={size}
        style={{
          position: "absolute",
          transform: "rotate(-90deg)",
        }}
      >
        <circle
          stroke="#EFF2F5"
          cx={R + effectiveStrokeWidth}
          cy={R + effectiveStrokeWidth}
          r={R}
          strokeWidth={effectiveStrokeWidth}
          fill="none"
          strokeDasharray={C}
        />
        <circle
          style={{ transition: "stroke-dashoffset 1s ease-out" }}
          stroke={color}
          cx={R + effectiveStrokeWidth}
          cy={R + effectiveStrokeWidth}
          r={R}
          strokeWidth={effectiveStrokeWidth}
          fill="none"
          strokeDasharray={C}
          strokeDashoffset={offset}
        />
      </svg>
      <div
        style={{
          width: "100%",
          height: "100%",
          position: "absolute",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontSize: R / 2,
          color: color,
        }}
      >
        <div>{value}%</div>
      </div>
    </div>
  );
};

