```javascript
function countSmallerNumbersAfterSelf(nums) {
  const countArray = new Array(nums.length).fill(0);

  function merge(arr, lo, mid, hi) {
    let i = lo,
        j = mid + 1,
        k = 0,
        rightCount = 0;
    const tempArr = new Array(hi - lo + 1).fill(0);

    while (i <= mid && j <= hi) {
      if (arr[i][0] > arr[j][0]) {
        rightCount++;
        tempArr[k++] = arr[j++];
      } else {
        countArray[arr[i][1]] += rightCount; 
        tempArr[k++] = arr[i++];
      }
    }

    while (i <= mid) {
      countArray[arr[i][1]] += rightCount; 
      tempArr[k++] = arr[i++];
    }

    while (j <= hi) {
      tempArr[k++] = arr[j++];
    }

    for (let i = lo, k = 0; i <= hi; i++, k++) {
      arr[i] = tempArr[k];
    }
  }

  function mergeSort(arr, lo, hi) {
    if (lo >= hi) return;
    const mid = lo + Math.floor((hi - lo) / 2);
    mergeSort(arr, lo, mid);
    mergeSort(arr, mid + 1, hi);
    merge(arr, lo, mid, hi);
  }

  const numsWithIndex = nums.map(function(num, index) {
    return [num, index];
  });
  mergeSort(numsWithIndex, 0, nums.length - 1);

  return countArray;
}
```

