#include <iostream>
#include <string>
using namespace std;

int main() {
    string candidates[10];
    string courses[5] = {"Math", "Science", "English", "History", "Art"};
    string course_codes[] = {"MAT", "SCI", "ENG", "HIS", "ART"};
    int marks[10][5];
    char grades[10][5];

    // Request the user to enter names of 10 candidates and their respective marks in 5 courses for the semester
    for (int i = 0; i < 10; i++) {
        cout << "Enter name of candidate " << i+1 << ": ";
        getline(cin, candidates[i]);
        for (int j = 0; j < 5; j++) {
            cout << "Enter marks for " << courses[j] << " (out of 100): ";
            cin >> marks[i][j];

            // Assign grades based on marks
            if (marks[i][j] >= 90) {
                grades[i][j] = 'A';
            } else if (marks[i][j] >= 80) {
                grades[i][j] = 'B';
            } else if (marks[i][j] >= 70) {
                grades[i][j] = 'C';
            } else if (marks[i][j] >= 60) {
                grades[i][j] = 'D';
            } else {
                grades[i][j] = 'F';
            }
        }
        cout << endl;
        cin.ignore(); // Ignore newline character from cin
    }

    // Display the names of the candidates, their respective course names, course code, marks and grade
    cout << "Candidate\t";
    for (int j = 0; j < 5; j++) {
        cout << courses[j] << "\t";
    }
    cout << endl;

    for (int i = 0; i < 10; i++) {
        cout << candidates[i] << "\t\t";
        for (int j = 0; j < 5; j++) {
            cout << course_codes[j] << marks[i][j] << "(" << grades[i][j] << ")" << "\t";
        }
        cout << endl;
    }
    return 0;
}
