    for (int i = 0; i < contadorAlunos; i++) {
        if (alunos[i].getNumProcesso() == numProcesso) {
            alunos[i].mostrarDados();
            encontrado = true;
            break;
        }
    }

    if (!encontrado) {
        std::cout << "Aluno não encontrado!" << std::endl;
    }
}

void atualizarAluno() {
