// Faz a pesquisa pelo nome do aluno e retorna o índice do aluno no vetor de alunos ou -1 se não encontrado
int pesquisarAluno(Aluno* alunos[], int numAlunos, const std::string &nome) {
    for (int i = 0; i < numAlunos; i++) {
        if (alunos[i]->getNome() == nome) {
            return i;
        }
    }
    return -1;
}

void mostrarAluno(const Aluno* aluno) {
    std::cout << "Número do processo: " << aluno->getNumProcesso() << std::endl;
    std::cout << "Nome: " << aluno->getNome() << std::endl;
    std::cout << "Morada: " << aluno->getMorada() << std::endl;
    std::cout << "Data de nascimento: " << aluno->getDataNasc() << std::endl;
    std::cout << "Ano de escolaridade: " << aluno->getAnoEscolaridade() << std::endl;
}

void mostrarAlunos(Aluno* alunos[], int numAlunos) {
    for (int i = 0; i < numAlunos; i++) {
        std::cout << "Aluno " << (i + 1) << ":" << std::endl;
        mostrarAluno(alunos[i]);
        std::cout << "--------------------------" << std::endl;
