function convert_currency_to_egp($cart)
{
    if (is_admin() && !defined('DOING_AJAX')) return;

    // Set the exchange rate for USD to EGP (update this value regularly)
    $exchange_rate = 31.07;

    // Loop through the cart items
    foreach ($cart->get_cart() as $cart_item_key => $cart_item) {
        $product = $cart_item['data'];

        // If the product price is in USD, convert it to EGP
        if (get_option('woocommerce_currency') == 'USD') {

            $egp_price = $product->get_price() * $exchange_rate;
            $product->set_price($egp_price);

            // Update the cart item currency to EGP
            $cart_item['line_total'] = floatval($cart_item['line_total']) * $exchange_rate;
            $cart_item['line_subtotal'] = floatval($cart_item['line_subtotal']) * $exchange_rate;
        }
    }
}
