import React, { useState } from ‘react’;
import { ChargesTable, paymentTableSelectors, PaymentChargesTableAction } from ‘@txp-core/payment-transactions-table’;
import { Table } from ‘@ids-ts/table’;
import { useTXPStateRead, useTXPStateWrite } from ‘@txp-core/runtime’;
import { useFormatMessage } from ‘@txp-core/intl’;
import { TransactionActions, TransactionSelectors } from ‘@txp-core/transactions-core’;
import TextField from ‘@ids-ts/text-field’;
import { enabled } from ‘@txp-core/enabled’;
import { TransactionTypes, getTxnUrl } from ‘@txp-core/transactions-utils’;
import { toLocalizedMoneyString, toLocalDateFormat } from ‘@txp-core/basic-utils’;
import TransactionsTableHeader from ‘./TransactionTableHeaders’;
import styles from ‘src/js/staticLayout/Grid.module.css’;
import Checkbox from ‘@ids-ts/checkbox’;
