App.tsx

```tsx
import React, { useState } from "react";
import "./App.css";

type GameLog = {
  date: string;
  risk: string;
  randomValue: number;
  money: number;
  chance: number;
};

const getDefaultState = () => ({
  chance: 10,
  randomValue: 0,
  money: 500,
  gameLogs: [] as GameLog[],
});

const App: React.FC = () => {
  const [state, setState] = useState(getDefaultState);

  const riskRanges = {
    lowRisk: [-25, 100],
    moderateRisk: [-100, 1000],
    highRisk: [-500, 2500],
    severeRisk: [-3000, 5000],
  };

  const onBetClick = (risk: keyof typeof riskRanges) => {
    if (state.chance === 0) return;

    const [min, max] = riskRanges[risk];
    const randomValue = Math.floor(Math.random() * (max - min + 1)) + min;
    const money = state.money + randomValue;
    const chance = state.chance - 1;

    setState((prevState) => ({
      ...prevState,
      money,
      chance,
      randomValue,
      gameLogs: [
        ...prevState.gameLogs,
        {
          date: new Date().toLocaleString("en-US", { timeZone: "Asia/Singapore" }),
          risk: risk.replace(/([A-Z])/g, " $1").trim(), // Convert camelCase to Title Case
          randomValue,
          money,
          chance,
        },
      ],
    }));
  };

  const onResetClick = () => {
    setState(getDefaultState());
  };

  return (
    <div id="container">
      <header>
        <button onClick={onResetClick}>Reset Game</button>
        <h1>Your money: {state.money}</h1>
        <h2>Chances left: {state.chance}</h2>
      </header>
      <section id="bets">
        {Object.keys(riskRanges).map((risk) => (
          <div key={risk}>
            <h1>{risk.replace(/([A-Z])/g, " $1").trim()}</h1> // Convert camelCase to Title Case
            <button onClick={() => onBetClick(risk as keyof typeof riskRanges)}>Bet</button>
            <p>by {riskRanges[risk].join(" up to ")}</p>
          </div>
        ))}
      </section>
      <section id="gameHost">
        <h3>Game Host:</h3>
        <div id="gameLogs">
          <p>
            [ {new Date().toLocaleString("en-US", { timeZone: "Asia/Singapore" })} ] Welcome to Money Button Game, risk
            taker! All you need to do is push buttons to try your luck. You have free 10 chances with initial money 500.
            Choose wisely and good luck!"
          </p>
          {state.gameLogs.map((log, idx) => (
            <p key={idx} className={log.randomValue > 0 ? "won" : "lost"}>
              [ {log.date} ] You pushed "{log.risk}". Value is {log.randomValue}. Your current money now is{" "}
              {log.money} with {log.chance} chance(s) left.
            </p>
          ))}
          {state.chance < 1 && <h4>Game Over!</h4>}
        </div>
      </section>
    </div>
  );
};

export default App;
```

