/* global select, createElement, windowHeight, windowWidth */
let inputTextarea, analyzeBtn, outputDiv;

function setup() {
  createCanvas(windowWidth, windowHeight);
  inputTextarea = createElement("textarea");
  inputTextarea.attribute("placeholder", "Paste your code here...");
  inputTextarea.size(windowWidth / 2, windowHeight / 2);
  inputTextarea.position(windowWidth / 4, windowHeight / 4);

  analyzeBtn = createButton("Analyze");
  analyzeBtn.position(windowWidth / 2 - analyzeBtn.width / 2, inputTextarea.y + inputTextarea.height + 5);
  analyzeBtn.mousePressed(analyzeCode);

  outputDiv = createElement("div");
  outputDiv.position(inputTextarea.x, analyzeBtn.y + analyzeBtn.height + 5);
}

function draw() {
  background(220);
}

function analyzeCode() {
  const code = inputTextarea.value();
  const regex = /<script.*?src="(.*?)"/gi;
  let matches, outputStr = "";

  while ((matches = regex.exec(code)) !== null) {
    outputStr += matches[1] + "<br>";
  }

  outputDiv.html(outputStr);
}
