import { Maa } from "./moduulit/maa.js";
import { Kortti } from "./moduulit/kortti.js";
import { Pakka } from "./moduulit/pakka.js";
import { Kasi } from "./moduulit/kasi.js";

let pakka;
let pelaajanKasi;
let jakajanKasi;

init();

function init() {
  document.getElementById("ota-kortti").onclick = otaKortti;
  document.getElementById("jaa").onclick = jaa;
  document.getElementById("uusi-peli").onclick = uusiPeli;
}

function uusiPeli() {
  pakka = Pakka.luoPakka();
  pakka.sekoita();
  pelaajanKasi = Kasi.luoKasi();
  jakajanKasi = Kasi.luoKasi();

  pelaajanKasi.lisaaKortti(pakka.otaKortti());
  jakajanKasi.lisaaKortti(pakka.otaKortti());

  paivitaKortit();
  document.getElementById("tulos").innerText = "";
}

function otaKortti() {
  pelaajanKasi.lisaaKortti(pakka.otaKortti());
  paivitaKortit();

  if (pelaajanKasi.summa > 21) {
    document.getElementById("tulos").innerText = "Hävisit!";
    document.getElementById('ota-kortti').disabled = true;
    document.getElementById('jaa').disabled = true;
  }
}

function jaa() {
  while (jakajanKasi.summa < 17) {
    jakajanKasi.lisaaKortti(pakka.otaKortti());
  }

  document.getElementById('ota-kortti').disabled = true;
  document.getElementById('jaa').disabled = true;

  paivitaKortit();

  if (jakajanKasi.summa > 21 || pelaajanKasi.summa > jakajanKasi.summa) {
    document.getElementById("tulos").innerText = "Voitit!";
  } else if (pelaajanKasi.summa === jakajanKasi.summa) {
    document.getElementById("tulos").innerText = "Tasapeli";
  } else {
    document.getElementById("tulos").innerText = "Hävisit!";
  }
}

function paivitaKortit() {
  for (let i = 0; i < 5; i++) {
    let pelaajanKortti = document.getElementById(`pelaaja${i + 1}`);
    let jakajanKortti = document.getElementById(`jakaja${i + 1}`);

    if (i < pelaajanKasi.kortteja) {
      let kortti = pelaajanKasi.kortit[i];
      pelaajanKortti.innerHTML = `<span class="${kortti.maa.vari}">${kortti.arvosymboli}</span><span class="symboli ${kortti.maa.vari}">${kortti.maa.symboli}</span><span class="tokanumero ${kortti.maa.vari}">${kortti.arvosymboli}</span>`;
    } else {
      pelaajanKortti.innerHTML = "";
    }

    if (i < jakajanKasi.kortteja) {
      let kortti = jakajanKasi.kortit[i];
      jakajanKortti.innerHTML = `<span class="${kortti.maa.vari}">${kortti.arvosymboli}</span><span class="symboli ${kortti.maa.vari}">${kortti.maa.symboli}</span><span class="tokanumero ${kortti.maa.vari}">${kortti.arvosymboli}</span>`;
    } else {
      jakajanKortti.innerHTML = "";
    }
  }
}
