const sectors = [
  '1',
  '2',
  '3',
  '4',
  '5',
  '6'
];
const sectorCount = sectors.length;
const sectorAngle = 360 / sectorCount;
const sectorSize = 100 / sectorCount;

const rouletteElem = document.querySelector('.roulette');
const sectorsElem = document.querySelector('.sectors');
const spinElem = document.querySelector('.spin');

let currentSector = null;
let isSpinning = false;

// Calculate the angle to rotate the wheel
function getSpinAngle(winningSector) {
  const targetAngle = (winningSector - 1) * sectorAngle;
  const rounds = 5; // Number of rounds before stopping
  const extraDegrees = 30; // Extra degrees the wheel will spin after stopping
  return targetAngle + rounds * 360 + extraDegrees;
}

// Spin the wheel to the given angle
function spin(angle) {
  isSpinning = true;
  rouletteElem.style.transform = `rotate(-${angle}deg)`;
}

// Stop the wheel at the given sector
function stopAt(sector) {
  const sectorAngle = (sector - 1) * sectorAngle;
  const spinAngle = sectorAngle + sectorAngle / 2;
  rouletteElem.style.transform = `rotate(-${spinAngle}deg)`;
}

// Handle spinning the wheel
function handleSpin() {
  if (isSpinning) return;

  // Call backend to get winning sector
  fetch('/api/winning-sector')
    .then(res => res.json())
    .then(winningSector => {
      // Calculate the angle to rotate the wheel
      const spinAngle = getSpinAngle(winningSector);

      // Spin the wheel
      spin(spinAngle);

      // Stop the wheel after some time and highlight the winning sector
      setTimeout(() => {
        currentSector = winningSector;
        stopAt(currentSector);
        const activeSectorElem = sectorsElem.querySelector(`.sector:nth-child(${winningSector})`);
        activeSectorElem.classList.add('active');
        isSpinning = false;
      }, 5000);
    })
    .catch(err => console.error(err));
}

// Initialize the wheel sectors
sectors.forEach((sectorText, index) => {
  const angle = index * sectorAngle;
  const sectorElem = document.createElement('li');
  sectorElem.className = 'sector';
  sectorElem.style.transform = `rotate(${angle}deg) translate(0, -100px) rotate(-${angle}deg)`;
  sectorElem.textContent = sectorText;
  sectorsElem.appendChild(sectorElem);
});

// Handle spinning the wheel when the button is clicked
spinElem.addEventListener('click', handleSpin);
