const otaKorttiNappi = document.getElementById("ota-kortti");
const jaaNappi = document.getElementById("jaa");
const uusiPeliNappi = document.getElementById("uusi-peli");

otaKorttiNappi.addEventListener("click", otaKortti);
jaaNappi.addEventListener("click", jaa);
uusiPeliNappi.addEventListener("click", aloitaPeli);

function aloitaPeli() {
  pakka = Pakka.luoPakka();
  pakka.sekoita();

  pelaaja = Kasi.luoKasi();
  jakaja = Kasi.luoKasi();

  otaKortti(pelaaja, "pelaaja");
  otaKortti(jakaja, "jakaja");

  otaKorttiNappi.disabled = false;
  jaaNappi.disabled = false;
  document.getElementById("tulos").innerText = "";
}

function otaKortti() {
  if (pelaaja.kortteja < 5) {
    otaKortti(pelaaja, "pelaaja");
  }

  if (pelaaja.summa > 21) {
    lopetaPeli("Pelaaja hävisi. Summa yli 21.");
  } else if (pelaaja.kortteja === 5) {
    lopetaPeli("Pelaaja voitti! Viisi korttia eikä yli 21.");
  }
}

function jaa() {
  while (jakaja.summa < 17 && jakaja.kortteja < 5) {
    otaKortti(jakaja, "jakaja");
  }

  if (jakaja.summa > 21 || pelaaja.summa > jakaja.summa) {
    lopetaPeli("Pelaaja voitti!");
  } else {
    lopetaPeli("Jakaja voitti!");
  }
}

function lopetaPeli(tulos) {
  otaKorttiNappi.disabled = true;
  jaaNappi.disabled = true;
  document.getElementById("tulos").innerText = tulos;
}

