  kasi.lisaaKortti(kortti);

  const index = kasi.kortteja;
  const korttiElement = document.getElementById(`${elementIdPrefix}${index}`);
  korttiElement.classList.add(kortti.maa.vari);
  korttiElement.innerHTML = `
