Here is the complete `kayttoliittyma.js` code based on the provided instructions and the code for the other JavaScript modules (`kasi.js`, `kortti.js`, `maa.js`, and `pakka.js`). This code connects the modules, sets up the Ventti game, and ensures that the HTML buttons work properly.

```javascript
import { Kasi } from "./moduulit/kasi.js";
import { Pakka } from "./moduulit/pakka.js";

let pelaaja, jakaja, pakka, tulos, napit, korttiElementit;

function alusta() {
  pelaaja = Kasi.luoKasi();
  jakaja = Kasi.luoKasi();
  pakka = Pakka.luoPakka();

  tulos = document.getElementById("tulos");
  napit = {
    otaKortti: document.getElementById("ota-kortti"),
    jaa: document.getElementById("jaa"),
    uusiPeli: document.getElementById("uusi-peli")
  };

  korttiElementit = {
    pelaaja: document.querySelectorAll("#pelaajan-kortit .kortti"),
    jakaja: document.querySelectorAll("#jakajan-kortit .kortti")
  };

  napit.otaKortti.addEventListener("click", otaKortti);
  napit.jaa.addEventListener("click", jaa);
  napit.uusiPeli.addEventListener("click", uusiPeli);

  uusiPeli();
}

function uusiPeli() {
  pelaaja.kortit = [];
  jakaja.kortit = [];

  pakka = Pakka.luoPakka();
  pakka.sekoita();

  tulos.textContent = "Aloita peli painamalla 'Ota kortti' -nappia.";

  paivitaKortit();
}

function otaKortti() {
  if (!pelaaja.kortteja) {
    tulos.textContent = "Ota kortti";
  }

  pelaaja.lisaaKortti(pakka.otaKortti());
  tarkistaJaPaivita();
}

function jaa() {
  while (jakaja.summa < 17) {
    jakaja.lisaaKortti(pakka.otaKortti());
  }

  const pelaajanSumma = pelaaja.summa;
  const jakajanSumma = jakaja.summa;

  if ((pelaajanSumma <= 21 && pelaajanSumma > jakajanSumma) || jakajanSumma > 21) {
    tulos.textContent = "Voitit!";
  } else if (pelaajanSumma === jakajanSumma) {
    tulos.textContent = "Tasapeli!";
  } else {
    tulos.textContent = "Hävisit!";
  }

  paivitaKortit(true);
}

function tarkistaJaPaivita() {
  paivitaKortit();

  if (pelaaja.summa > 21) {
    tulos.textContent = "Hävisit!";
  }
}

function paivitaKortit(jakajanKortitNakyviin = false) {
  for (let i = 0; i < pelaaja.kortteja; i++) {
    korttiElementit.pelaaja[i].innerHTML = naytaKortti(pelaaja.kortit[i]);
  }

  for (let i = 0; i < jakaja.kortteja; i++) {
    if (jakajanKortitNakyviin) {
      korttiElementit.jakaja[i].innerHTML = naytaKortti(jakaja.kortit[i]);
    } else {
      korttiElementit.jakaja[i].innerHTML = `<span>${i === 0 ? naytaKortti(jakaja.kortit[i]) : ""}</span>`;
    }
  }
}

function naytaKortti(kortti) {
  const arvo = kortti.arvosymboli;
  const vari = kortti.maa.vari;
  const symboli = kortti.maa.symboli;

  return `<span class="${vari}">${arvo}${symboli}</span>`;
}

alusta();
