Here is the translation from Python to C++ code:

```cpp
#include <iostream>
#include <vector>

std::vector<std::vector<int>> matrix_mult(const std::vector<std::vector<int>>& a, const std::vector<std::vector<int>>& b) {
    size_t a_rows = a.size();
    size_t a_cols = a[0].size();
    size_t b_rows = b.size();
    size_t b_cols = b[0].size();

    if (a_cols != b_rows) {
        throw std::invalid_argument("Matrix dimensions do not match for multiplication.");
    }

    std::vector<std::vector<int>> result(a_rows, std::vector<int>(b_cols, 0));

    for (size_t i = 0; i < a_rows; ++i) {
        for (size_t j = 0; j < b_cols; ++j) {
            for (size_t k = 0; k < a_cols; ++k) {
                result[i][j] += a[i][k] * b[k][j];
            }
        }
    }

    return result;
}

int main() {
    std::vector<std::vector<int>> a = {
        {1, 2, 3},
        {4, 5, 6}
    };

    std::vector<std::vector<int>> b = {
        {7, 8},
        {9, 10},
        {11, 12}
    };

    std::vector<std::vector<int>> result = matrix_mult(a, b);

    for (const auto& row : result) {
        for (int val : row) {
            std::cout << val << " ";
        }
        std::cout << std::endl;
    }

    return 0;
}
```

