using NDArray = std::vector<std::vector<double>>;

class MyClass {
private:
    std::pair<double, double> _area_range;

public:
    MyClass(std::pair<double, double> area_range) : _area_range(area_range) {}

    std::vector<size_t> select_boxes_by_area(const NDArray& boxes_xyxy) {
        if (_area_range.first < 0 || _area_range.second < 0) {
            throw std::invalid_argument("Area range must be specified to filter boxes by area");
        }

        std::vector<double> areas(boxes_xyxy.size());
        for (size_t i = 0; i < boxes_xyxy.size(); ++i) {
            areas[i] = (boxes_xyxy[i][2] - boxes_xyxy[i][0]) * (boxes_xyxy[i][3] - boxes_xyxy[i][1]);
        }

        std::vector<size_t> selected;
        for (size_t i = 0; i < areas.size(); ++i) {
            if (areas[i] >= _area_range.first && areas[i] <= _area_range.second) {
                selected.push_back(i);
            }
        }
        return selected;
    }
