    public static string ModifyString(string str, int n)
    {
        string[] lines = str.Split('\n');
        for(int i = 0; i < lines.Length; i++)
        {
            string line = lines[i];
            if(n < line.Length)
            {
                line = line.Insert(n, "*");
            }
            lines[i] = line;
        }
        return string.Join("\n", lines);
    }

    // Процедура, которая добавляет звездочки к строке справа
    public static string ModifyString2(string str, int n)
    {
        string[] lines = str.Split('\n');
        for(int i = 0; i < lines.Length; i++)
        {
            string line = lines[i];
            while(line.Length <= n)
            {
                line += "*";
            }
            lines[i] = line;
        }
        return string.Join("\n", lines);
    }
