        for row in self.field:
            for col in row:
                out += "{:>4}".format(col)
            out += "\n"
        return out


class Bear:
    DIRECTIONS = {'N': (-1, 0), 'S': (1, 0), 'E': (0, 1), 'W': (0, -1),
                  'NE': (-1, 1), 'NW': (-1, -1), 'SE': (1, 1), 'SW': (1, -1)}

    def __init__(self, row, col, direction):
        self.row = row
        self.col = col
        self.direction = direction

    def move(self, field):
        dr, dc = self.DIRECTIONS[self.direction]
        self.row += dr
        self.col += dc
        if 0 <= self.row < len(field) and 0 <= self.col < len(field[0]):
            return True
        return False

    def __str__(self):
        return f"Bear at ({self.row}, {self.col}) moving {self.direction}"


