import java.security.MessageDigest;

public class MD5Utils {

    public static String generateMd5(long wmPoiId, long noticeId, String text) {
        String input = wmPoiId + "" + noticeId + text;
        try {
            MessageDigest md = MessageDigest.getInstance("MD5");
            byte[] md5Hash = md.digest(input.getBytes());
            StringBuilder sb = new StringBuilder();
            for (byte b : md5Hash) {
                sb.append(String.format("%02x", b & 0xff));
            }
            return sb.toString();
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        }
    }

    public static void main(String[] args) {
        long wmPoiId = 123456789L;
        long noticeId = 987654321L;
        String text = "abcdefg";
        String md5 = generateMd5(wmPoiId, noticeId, text);
        System.out.println("md5: " + md5);
    }
}
