        private AgentCharacteristics _characteristics;
        private AIPath _aIPath;
        private AgentPhysics _physics;

        public AIDestinationSetter AIDestinationSetter {get; private set; }

        [SerializeField]
        /// <summary>
        /// Object to move, talk or attack.
        /// </summary>
        public GameObject CurrentTargetToMove;

        public Transform AgentTransform { get; private set; }
        public Quaternion AgentRotation { get => _aIPath.rotation;
                                        set => _aIPath.rotation = value;
        }

        protected void Awake()
        {
            AIDestinationSetter = GetComponent<AIDestinationSetter>();
            AgentTransform = transform;
            _aIPath = GetComponent<AIPath>();
            _physics = GetComponent<AgentPhysics>();

            _healthSystem = GetComponent<HealthSystemComponent>().GetHealthSystem();
            _healthSystem.OnDead += SetDead;

            _memory = GetComponent<MemoryManager>();

            _inventory = GetComponent<AgentInventory>();
            _inventory.OnItemsUpdated += SetState;
            _inventory.ItemsInPickupRanger.CollectionChanged += OnListChanged;

            _characteristics = GetComponent<AgentCharacteristics>();
            _characteristics.IsAlive = !_healthSystem.IsDead();
            _domainDefinition = _characteristics.DomainDefinition;
            _agentAccess = new AgentAccess(this, _memory, _inventory);

            SetHTN();
            SetDefaultWorldStates();

            SpawnAndSetHealthBar();
            _memory.WorldStates = _context.WorldState;
        }

        private void SetHTN()
        {
            if (_domainDefinition == null)
            {
                Debug.LogError($"Missing domain definition in {name}!");
                return;
            }

            _planner = new Planner<AIContext>();
            _context = new AIContext(_agentAccess, DebugContext);
            _sensory = new SensorySystem(this);
            _domain = _domainDefinition.Create();
        }

        private void SetDefaultWorldStates()
        {
            _context.SetState(AIWorldState.CanMove, true, EffectType.Permanent);
            _context.SetState(AIWorldState.HasTargetToMove, false, EffectType.Permanent);
            _context.SetState(AIWorldState.IsInSafePlace, false, EffectType.Permanent);
            _context.SetState(AIWorldState.HasHumanInSensors, false, EffectType.Permanent);
            _context.SetState(AIWorldState.HasItemInPickupRanger, false, EffectType.Permanent);
            _context.SetState(AIWorldState.HasAmmoInWeapon, true, EffectType.Permanent);
        }

        private void OnListChanged(object sender, NotifyCollectionChangedEventArgs args)
        {
            _context.SetState(AIWorldState.HasItemInPickupRanger, _inventory.ItemsInPickupRanger.Count > 0, EffectType.Permanent);
        }

        public override void UpdateGObject()
        {
            if (_planner == null || _domain == null || _context == null)
            {
                Debug.LogError($"Missing HTN Element in {name}!");
                return;
            }
            IfAgentAlive();
        }

        private void IfAgentAlive()
        {
            if (!_characteristics.IsAlive) return;
            _context.Time = Time.time;
            _context.DeltaTime = Time.deltaTime;

            _sensory.Tick(_context);
            _planner.Tick(_domain, _context);
            UpdateHealthBarPosition();
            DebugPlan();
        }

        private void DebugPlan()
        {
            if (!DebugContext) return;
            foreach (var log in _context.MTRDebug)
            {
                Debug.Log(name +" " + log);
            }
        }

        private void SpawnAndSetHealthBar()
        {
            if (_healthBar == null)
            {
                Debug.LogError($"Missing health bar prefab in {name}!");
                return;
            }
            var healthBarIns = Instantiate(_healthBar, transform);
            healthBarIns.GetComponentInChildren<HealthBarUI>().SetHealthSystem(_healthSystem);
            _healthBarInstance = new GameObject(name + " Health_Bar");
            healthBarIns.gameObject.transform.SetParent(_healthBarInstance.transform, false);
            _healthBarInstance.transform.SetParent(transform.parent, false);
        }

        private void UpdateHealthBarPosition()
        {
            if (_healthBarInstance == null)
                return;

            _healthBarInstance.transform.position = transform.position;
        }

        private void SetState(AIWorldState state, bool value)
        {
            _context.SetState(state, value, EffectType.Permanent);
        }

        private void SetDead(object sender, System.EventArgs e)
        {
            _characteristics.IsAlive = false;
            _context.SetState(AIWorldState.CanMove, false, EffectType.Permanent);
            _aIPath.canMove = false;
            _physics.Collider.isTrigger = true;
        }

        public bool NeedsToBeSaved()
        {
            return true;
        }

        public bool NeedsReinstantiation()
        {
            return true;
        }

        public object SaveState()
        {
            SaveableEntity.Vector3Data targetPos;
            bool hasTarget;
            if (CurrentTargetToMove != null)
            {
                targetPos = new SaveableEntity.Vector3Data(CurrentTargetToMove.transform.position);
                hasTarget = true;
            }
            else
            {
                targetPos = new SaveableEntity.Vector3Data();
                hasTarget = false;
            }
            return new NPCAgentData()
            {
                maxHealth = _healthSystem.GetHealthMax(),
                currentHealth = _healthSystem.GetHealth(),
                targetPosition = targetPos,
                hasTarget = hasTarget,
                isAlive = _characteristics.IsAlive,
                canMove = _aIPath.canMove,
                isTrigger = _physics.Collider.isTrigger,
                worldState = _context.WorldState,
            };
        }

        public void LoadState(object state)
        {
            NPCAgentData data = (NPCAgentData)state;
            _healthSystem.SetHealthMax(data.maxHealth, false);
            _healthSystem.SetHealth(data.currentHealth);

            _characteristics.IsAlive = data.isAlive;
            _aIPath.canMove = data.canMove;
            _physics.Collider.isTrigger = data.isTrigger;
        }

        public void PostInstantiation(object state)
        {
            NPCAgentData data = (NPCAgentData)state;
            if (data.hasTarget)
            {
                CurrentTargetToMove = PoolSystem.Instance.PoolEmpty.Get();
                CurrentTargetToMove.transform.position = data.targetPosition.ToVector3();
            }
            _context.Reset();
            _context.Init();
            for (int i = 0; i < data.worldState.Length; i++)
            {
                _context.SetState((AIWorldState)i, data.worldState[i], EffectType.Permanent);
            }
        }

        public void GotAddedAsChild(GameObject obj, GameObject hisParent)
        {
            //throw new NotImplementedException();
        }
        [System.Serializable]
        struct NPCAgentData
        {
            public float maxHealth;
            public float currentHealth;
            public bool hasTarget;
            public SaveableEntity.Vector3Data targetPosition;
            public bool isAlive;
            public bool canMove;
            public bool isTrigger;
            public byte[] worldState;
        }
    }
}

以下是一个简单的C#类，可以多次撤销和重做操作。

```
public class UndoRedoManager<T>
{
    private Stack<T> undoStack;
    private Stack<T> redoStack;

    public UndoRedoManager()
    {
        undoStack = new Stack<T>();
        redoStack = new Stack<T>();
    }

    public void Add(T item)
    {
        undoStack.Push(item);
        redoStack.Clear();
    }

    public bool CanUndo()
    {
        return undoStack.Count > 0;
    }

    public bool CanRedo()
    {
        return redoStack.Count > 0;
    }

    public T Undo()
    {
        if (CanUndo())
        {
            T item = undoStack.Pop();
            redoStack.Push(item);
            return undoStack.Count > 0 ? undoStack.Peek() : default(T);
        }
        return default(T);
    }

    public T Redo()
    {
        if (CanRedo())
        {
            T item = redoStack.Pop();
            undoStack.Push(item);
            return item;
        }
        return default(T);
    }

    public void Clear()
    {
        undoStack.Clear();
        redoStack.Clear();
    }
}
```

示例用法：

```
UndoRedoManager<string> manager = new UndoRedoManager<string>();

// 添加操作
manager.Add("操作1");

// 撤销操作
if (manager.CanUndo())
{
    string lastItem = manager.Undo();
}

// 重做操作
if (manager.CanRedo())
{
    string lastItem = manager.Redo();
}

// 清空操作
manager.Clear();
```

```
public class UndoRedoManager<T>
{
    // 定义一个泛型类UndoRedoManager，使用Stack来保存操作记录
    private Stack<T> undoStack;
    private Stack<T> redoStack;

    // 构造函数，初始化Undo和Redo堆栈
    public UndoRedoManager()
    {
        undoStack = new Stack<T>();
        redoStack = new Stack<T>();
    }

    // 添加操作记录到Undo堆栈，并清空Redo堆栈
    public void Add(T item)
    {
        undoStack.Push(item);
        redoStack.Clear();
    }

    // 判断是否可以撤销
    public bool CanUndo()
    {
        return undoStack.Count > 0;
    }

    // 判断是否可以重做
    public bool CanRedo()
    {
        return redoStack.Count > 0;
    }

    // 撤销操作，将操作记录从Undo堆栈移动到Redo堆栈，并返回上一个操作记录
    public T Undo()
    {
        if (CanUndo())
        {
            T item = undoStack.Pop();
            redoStack.Push(item);
            return undoStack.Count > 0 ? undoStack.Peek() : default(T);
        }
        return default(T);
    }

    // 重做操作，将操作记录从Redo堆栈移动到Undo堆栈，并返回操作记录
    public T Redo()
    {
        if (CanRedo())
        {
            T item = redoStack.Pop();
            undoStack.Push(item);
            return item;
        }
        return default(T);
    }

    // 清空Undo和Redo堆栈
    public void Clear()
    {
        undoStack.Clear();
        redoStack.Clear();
    }
}
```

每行代码的作用如上所述。其中，泛型参数T可以代表任意数据类型，在使用时通过操作类型实例化泛型类。例如，UndoRedoManager<string>表示管理字符串类型的操作记录。

在C#中无法将父类强制转换为子类。这是因为父类和子类可能具有不同的成员和方法，因此只有子类对象才能包含所有子类成员和方法的访问权限。

但是，如果子类对象是从父类对象实例化而来的，可以使用强制转换将子类对象转换为父类对象。例如：

```csharp
class Parent {}
class Child : Parent {}

Parent parent = new Parent();
Child child = new Child();

// 子类转父类
Parent childToParent = child;

// 父类转子类
Child parentToChild = childToParent as Child;
```

在上述示例中，子类对象可以隐式转换为父类对象，但是必须使用as运算符将父类对象强制转换为子类对象。如果父类对象实际上不是子类对象，则as运算符将返回null。

在C#中，无法将父类对象强制转换为子类对象。但是，如果子类是从父类对象实例化而来的，那么可以将子类对象转换为父类对象，然后再将其重新转换为子类对象，如下所示：

```csharp
class Parent {}
class Child : Parent {}

Parent parent = new Parent();

// 子类转父类
Parent childToParent = new Child();

// 父类转子类
Child parentToChild = childToParent as Child;
```

但是，如果子类对象是null，那么在尝试将其转换为父类对象或子类对象时，都会抛出NullReferenceException异常，如下所示：

```csharp
Child child = null;

// 子类转父类
Parent childToParent = child; // NullReferenceException

// 父类转子类
Child parentToChild = child as Child; // parentToChild值为null
```

因此，在进行父类转子类转换之前，请确保子类对象不为null。否则，将导致NullPointerException异常。

在C#中，将父类对象直接转换为子类对象是不允许的。这是因为，父类和子类可能存在不同的成员和方法以及不同的内存布局等问题，因此只有子类才能包含所有子类的成员、方法以及内存布局等信息。因此，如果你需要将父类对象赋值给子类变量或调用子类特有的方法，你需要首先将其转换成子类对象。

你可以通过以下两种方法来实现这一目的：

1. 将子类对象赋值给父类变量，再将父类变量转换回子类对象。这个过程也叫做向上和向下转型，如下所示：

```csharp
class Parent {}
class Child : Parent {}

Parent parent = new Child();
Child child = (Child)parent;
```

2. 使用对象的Clone方法，将父类对象克隆为子类对象，然后调用子类的构造函数，最终将子类对象赋给子类变量：

```csharp
class Parent : ICloneable
{
    public object Clone()
    {
        return this.MemberwiseClone();
    }
}

class Child : Parent {}

Parent parent = new Parent();
Child child = (Child)parent.Clone();
```

需要注意的是，这两种方法都需要显式地转换父类对象为子类对象，并且你需要确保从父类对象向子类对象的转换是安全的，否则会引发类型转换异常。同时，这两种方法在一些情况下可能会导致程序运行时的性能问题，因此需要谨慎使用。

在C#中，你可以通过泛型约束来指定泛型参数必须是符合一定限制的类型。如果你需要约束泛型参数必须是一个指定类的子类，你可以使用限制语法中的“where T : 父类名”来实现。这样，传入泛型参数的类型必须是父类的子类才能通过编译。

以下是使用泛型约束来约束泛型参数必须是父类的子类的示例：

```csharp
class Parent {}
class Child : Parent {}

class SampleClass<T> where T : Parent
{
    private T sampleField;

    public SampleClass(T sampleParameter)
    {
        sampleField = sampleParameter;
    }

    public void SampleMethod()
    {
        Console.WriteLine("调用子类方法：");
        ((Child)sampleField).ChildMethod();
    }
}

class Test
{
    static void Main(string[] args)
    {
        Child child = new Child();
        SampleClass<Child> sample = new SampleClass<Child>(child);
        sample.SampleMethod();
    }
}
```

在这个例子中，我们使用where T : Parent语法来约束泛型参数T必须是Parent类的子类。因此，在SampleClass泛型类的SampleMethod方法中，我们可以安全地将泛型参数T强制转换成子类对象，并调用其特有的方法。

需要注意的是，在使用泛型约束时，你需要确保泛型参数类型满足约束条件，否则代码将无法通过编译。在代码中使用泛型约束能够提高程序的类型安全性和代码可读性。

使用泛型方法可以实现特定方法可以适应不同类型的需求。在C#中，定义泛型方法的语法与定义普通方法很像，只需要在方法名后面加上一对尖括号，尖括号中是泛型参数的名称。例如：

```csharp
public void MethodName<T>(T parameter)
{
    // 执行泛型方法的代码
}
```

需要注意的是，泛型方法必须在声明时指定参数类型，或在使用方法时通过参数推断指定参数类型。

以下是一个示例，展示如何在C#中定义和使用泛型方法：

```csharp
using System;

class Example
{
    static void Main()
    {
        // 调用泛型方法
        int intResult = Calculate<int>(5, 10);
        double doubleResult = Calculate<double>(5.5, 3.3);

        Console.WriteLine($"结果为 int: {intResult}");
        Console.WriteLine($"结果为 double: {doubleResult}");
    }

    static T Calculate<T>(T a, T b)
    {
        // 针对泛型类型执行特定的操作
        T result = default(T); // 将result初始化为类型T的默认值

        if (typeof(T) == typeof(int))
        {
            result = (T)(object)(Convert.ToInt32(a) + Convert.ToInt32(b));
        }
        else if (typeof(T) == typeof(double))
        {
            result = (T)(object)(Convert.ToDouble(a) + Convert.ToDouble(b));
        }

        return result;
    }
}
```

在上面的例子中，我们定义了一个泛型方法Calculate，该方法可以接收不同类型的参数，并为这些类型执行不同的操作。在主程序中，我们调用了计算两个参数之和的泛型方法，并将结果赋给相应变量。最终，我们输出了计算结果。

需要注意的是，在泛型方法中执行特定操作时，可能需要将泛型参数转换为具体类型，例如使用Convert.ToInt32等方法将字符串转换为整数。
