export default {
  data() {
    return {
      images: [
        'https://via.placeholder.com/640x360?text=Slide+1',
        'https://via.placeholder.com/640x360?text=Slide+2',
        'https://via.placeholder.com/640x360?text=Slide+3',
      ],
      speed: 500,
      activeSlideIndex: 0,
    };
  },
  methods: {
    prevSlide() {
      this.activeSlideIndex =
        this.activeSlideIndex === 0
          ? this.images.length - 1
          : this.activeSlideIndex - 1;
