    nextSlide() {
      this.activeSlideIndex =
        this.activeSlideIndex === this.images.length - 1 ? 0 : this.activeSlideIndex + 1;
    },
    changeSlide(index) {
      this.activeSlideIndex = index;
    },
  },
};
