    ReleaseDC(NULL, hDC);

    // 遍历目录下所有的图片文件
    sprintf(filename, "./*.%s", argv[1]);
    hfind = FindFirstFile(filename, &finddata);
    if(hfind == INVALID_HANDLE_VALUE) {
        return 0;
    }
    do {
        if(finddata.dwFileAttributes & FILE_ATTRIBUTE_DIRECTORY) {
            continue;
        }
        if(isalpha(finddata.cFileName[0]) && strcmp(get_file_ext(finddata.cFileName), argv[1]) == 0) {
            // 打开图片文件
            strcpy(filename, finddata.cFileName);
            hDC = GetDC(NULL);
            hDCMem = CreateCompatibleDC(hDC);
            ReleaseDC(NULL, hDC);
            hBitmap = (HBITMAP) LoadImage(NULL, filename, IMAGE_BITMAP, 0, 0, LR_LOADFROMFILE | LR_CREATEDIBSECTION);
            SelectObject(hDCMem, hBitmap);
            // 获取文字尺寸
            GetTextExtentPoint32(hDCMem, TEXT, strlen(TEXT), &textSize);
            // 在图片上绘制水印
            SetBkMode(hDCMem, TRANSPARENT);
            SetTextColor(hDCMem, RGB(255, 255, 255));
            SelectObject(hDCMem, hFont);
            TextOut(hDCMem, 0, 0, TEXT, strlen(TEXT));
            // 保存加水印后的图片
            ext = strrchr(filename, '.');
            arglen = strlen(argv[1]);
            strcpy(outfilename, filename);
            strcpy(outfilename + strlen(outfilename) - arglen, "_watermark.");
            strcat(outfilename, argv[1]);
            width = (int) ((double) textSize.cx * 1.5);
            height = (int) ((double) textSize.cy * 1.5);
            SaveDC(hDCMem);
            hBitmap = CreateCompatibleBitmap(hDCMem, width, height);
            SelectObject(hDCMem, hBitmap);
            BitBlt(hDCMem, 0, 0, width, height, hDCMem, 0, 0, SRCCOPY);
            RestoreDC(hDCMem, -1);
            DeleteObject(hFont);
            DeleteObject(hBitmap);
            DeleteDC(hDCMem);
            SaveBitmap(outfilename, hBitmap);
        }
    } while(FindNextFile(hfind, &finddata));
    FindClose(hfind);

    return 0;
}
```

