          <input
            type="checkbox"
            :value="option.value"
            v-model="selectedValues"
            @change="handleParentChange(option)"
          />
          {{ option.label }}
        </label>
        <div class="custom-dropdown-submenu">
          <label
            v-for="child in option.children"
            :key="child.value"
            class="custom-dropdown-option"
          >
            <input
              type="checkbox"
              :value="child.value"
              v-model="selectedValues"
            />
            {{ child.label }}
          </label>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { option } from "./option";
export default {
  data() {
    return {
      visible: false,
      selectedValues: [],
      options: option,
    };
  },
  methods: {
    handleParentChange(option) {
      if (this.selectedValues.includes(option.value)) {
        this.selectAllDescendants(option);
      } else {
        this.deselectAllDescendants(option);
      }
    },
    selectAllDescendants(option) {
      // Recursively check all children and descendants
      const selectChildren = (children) => {
        children.forEach((child) => {
          if (!this.selectedValues.includes(child.value)) {
            this.selectedValues.push(child.value);
          }
          if (child.children) {
            selectChildren(child.children);
          }
        });
      };
      selectChildren(option.children);
    },
    deselectAllDescendants(option) {
      // Recursively uncheck all children and descendants
      const deselectChildren = (children) => {
        children.forEach((child) => {
          const index = this.selectedValues.indexOf(child.value);
          if (index !== -1) {
            this.selectedValues.splice(index, 1);
          }
          if (child.children) {
            deselectChildren(child.children);
          }
        });
      };
      deselectChildren(option.children);
    },
  },
};
</script>
