    return <p>You're on fire with {count} clicks!</p>;
  } else {
    return <p>Keep clicking to reach 5.</p>;
  }
}

function RandomJoke() {
  const [joke, setJoke] = useState("");

  const handleFetchJoke = async () => {
    const response = await fetch("https://api.chucknorris.io/jokes/random");
    const data = await response.json();
    setJoke(data.value);
  };

  return (
    <div>
      <button onClick={handleFetchJoke}>Get Chuck Norris Joke</button>
      <p>{joke}</p>
    </div>
  );
}

export default App;
