#endif // #if PLATFORM_USES_ES3 || PLATFORM_ANDROID

    ...

    if (FPlatformAtomics::InterlockedCompareExchangePointer(&GCurrentFence, FenceCounter, nullptr) != nullptr)
    {
        check(FPlatformAtomics::InterlockedCompareExchange(&GCurrentAsyncCompilationCount, 0, 1) == 1);
