- [stb_image_write](https://github.com/nothings/stb/blob/master/stb_image_write.h)

```c
#include <stdio.h>
#include <math.h>
#include "stb_image.h"
#include "stb_image_write.h"

unsigned char find_closest_palette_color(unsigned char pixel, unsigned char *palette, int palette_size) {
    int min_distance = 255;
    unsigned char closest_color;
    int distance;

    for (int i = 0; i < palette_size; ++i) {
        distance = abs(palette[i] - pixel);
        if (distance < min_distance) {
            min_distance = distance;
            closest_color = palette[i];
        }
    }

    return closest_color;
}

int main()
{
    int width, height, channels;
    unsigned char *image = stbi_load("input.png", &width, &height, &channels, 0);
    if (!image) {
        fprintf(stderr, "Error loading image\n");
        return 1;
    }

    int gray_channels = 1;
    size_t gray_img_size = width * height * gray_channels;
    unsigned char *gray_img = (unsigned char *)malloc(gray_img_size);
    if (!gray_img) {
        fprintf(stderr, "Unable to allocate memory for grayscale image\n");
        return 1;
    }

    unsigned char custom_palette[] = {0, 85, 170, 255};  // 4 colors
    int palette_size = sizeof(custom_palette) / sizeof(custom_palette[0]);
    unsigned char pixel;

    for (unsigned char *p = image, *pg = gray_img; p != image + width * height * channels; p += channels, pg += gray_channels) {
        pixel = (uint8_t)((*p + *(p + 1) + *(p + 2)) / 3.0);
        *pg = find_closest_palette_color(pixel, custom_palette, palette_size);
    }

    stbi_write_png("output.png", width, height, gray_channels, gray_img, width * gray_channels);

    stbi_image_free(image);
    free(gray_img);

    return 0;
}
```

