            command.artwork.allContractTokenIds
        ) {
            const tokenIds = !command.artwork.allContractTokenIds
                ? command.artwork.tokenIds
                : // retrieve tokenIds from contract address
                  await this.backfillArtworkService.getTokensFromContract(command.artwork.contractAddress);

            const newTokens = await this.backfillArtworkService.createTokens(tokenIds, command.artwork.contractAddress);

            // swap tokens and return if the tokenChanged
            tokenChanged = artwork.swapTokens(newTokens);
        }

        // retrieve collection to be added to if needed
        const collection = command.artwork.collection
            ? await this.collectionRepository.findBySlug(command.artwork.collection)
            : null;

        if (collection) artwork.linkCollection(collection);

        // save updated artwork
        await this.artworkRepository.save(artwork);

        // unrelate collection if command is null
        if (command.artwork.collection == null && artwork.getAggregate().collection) {
            await this.collectionRepository.removeEdition(artwork);
        }

        if (tokenChanged)
            this.backfill(
                artwork,
                artwork.getAggregate().tokens.map((st) => st.tokenId),
                command.artwork.contractAddress,
                command.artwork.allContractTokenIds,
            );
    }

    async backfill(artwork: ArtworkDomain, tokenIds: string[], contractAddress: string, allContractTokenIds: boolean) {
        const backfillTask = this.backfillArtworkService.backfill(
            artwork,
            // send null when backfilling all the contract
            allContractTokenIds ? [] : tokenIds,
            contractAddress,
        );

        const tokenRefreshTask = this.backfillArtworkService.refreshTokens(contractAddress, tokenIds);

        const marketplaceTask = this.backfillArtworkService.updateMarketplaceId(artwork);

        await Promise.all([backfillTask, marketplaceTask, tokenRefreshTask]);
    }
}

