    // 插入节点
    void insert(int val) {
        Node *node = new Node(val);
        Node *current = root;
        Node *parent = nullptr;
        while (current != nullptr) {
            parent = current;
            if (val < current->val) {
                current = current->left;
            } else if (val > current->val) {
                current = current->right;
            } else {
                return;
            }
        }
        node->parent = parent;
        if (parent == nullptr) {
            root = node;
        } else if (val < parent->val) {
            parent->left = node;
        } else {
            parent->right = node;
        }
        insertFixup(node);
    }

    // 删除节点
    void remove(int val) {
        Node *node = root;
        while (node != nullptr) {
            if (node->val == val) {
                break;
            } else if (node->val > val) {
                node = node->left;
            } else {
                node = node->right;
            }
        }
        if (node == nullptr || (node->left != nullptr && node->right != nullptr)) {
            return;
        }
        Node *child = node->left == nullptr ? node->right : node->left;
        if (child != nullptr) {
            child->parent = node->parent;
        }
        if (node == root) {
            root = child;
        } else if (node == node->parent->left) {
            node->parent->left = child;
        } else {
            node->parent->right = child;
        }
        if (node == nullptr || node->color == BLACK) {
            removeFixup(child, node->parent);
        }
        delete node;
    }

    // 中序遍历红黑树
    void inorderTraversal() {
        inorderTraversal(root);
    }
};

