import java.security.KeyFactory;
import java.security.PublicKey;
import java.security.Signature;
import java.security.spec.X509EncodedKeySpec;

public class VerifySignature {
    public static void main(String[] args) throws Exception {
        // 提取签名所需数据，假设明文数据为data，签名值为sig，公钥为pubKey
        byte[] data = "Hello, world!".getBytes();
        byte[] sig = new byte[] { ... };
        byte[] pubKey = new byte[] { ... };

        // 构造公钥对象
        X509EncodedKeySpec pubKeySpec = new X509EncodedKeySpec(pubKey);
        KeyFactory keyFactory = KeyFactory.getInstance("RSA");
        PublicKey publicKey = keyFactory.generatePublic(pubKeySpec);

        // 创建签名对象，使用公钥初始化
        Signature sigVerifier = Signature.getInstance("SHA256withRSA");
        sigVerifier.initVerify(publicKey);

        // 更新数据并验证签名
        sigVerifier.update(data);
        boolean isVerified = sigVerifier.verify(sig);

        // 输出验证结果
        if (isVerified) {
            System.out.println("Signature is valid!");
        } else {
            System.out.println("Invalid signature.");
        }
    }
}
```

