  Game_Enemy.prototype.robotParts = function () {
    return this.enemy().robot_parts.slice();
  };

  Game_Enemy.prototype.initRobotParts = function () {
    this._robot_parts = this.robotParts();
  };

  // Add functionality to enable/disable parts
  Game_Enemy.prototype.leftArmEnabled = function () {
    return this._robot_parts.leftArm.enabled;
  };

  Game_Enemy.prototype.rightArmEnabled = function () {
    return this._robot_parts.rightArm.enabled;
  };

  // Display robot parts in battle (enemies)
  Window_BattleEnemy.prototype.refresh = function () {
    this.contents.clear();
    for (var i = 0; i < this.maxItems(); i++) {
      var name = this.enemyName(i);
      var rect = this.itemRectForText(i);
      this.drawText(name, rect.x, rect.y, rect.width);
      var parts = this._enemies[i].robotParts();
      for (var j = 0; j < parts.length; j++) {
        this.drawText(
          parts[j].name,
          rect.x + rect.width * (j + 1),
          rect.y,
          rect.width
        );
      }
    }
  };

  // Alias original methods
  var _Enemy_initMembers = Game_Enemy.prototype.initMembers;
  var _BattleEnemy_initialize = Window_BattleEnemy.prototype.initialize;

  // Initialize robot parts for enemies on creation
  Game_Enemy.prototype.initMembers = function () {
    _Enemy_initMembers.call(this);
    this.initRobotParts();
  };

  // Initialize battle enemy window with robot parts display
  Window_BattleEnemy.prototype.initialize = function (x, y) {
    _BattleEnemy_initialize.call(this, x, y);
    this.refresh();
  };

  // Add robot parts to note tags of actors and enemies
  DataManager.processRobotPartsNotetags = function (group) {
    for (var n = 1; n < group.length; n++) {
      var obj = group[n];
      var notedata = obj.note.split(/[\r\n]+/);

      obj.robot_parts = [];

      for (var i = 0; i < notedata.length; i++) {
        var line = notedata[i];
        if (line.match(/<(?:ROBOT_PARTS_DIR):[ ]*(.*)>/i)) {
          var parts = String(RegExp.$1).split(',');
          for (var j = 0; j < parts.length; j++) {
            obj.robot_parts.push({
              type: DataManager.equipTypes()[j],
              id: Number(parts[j].trim()),
            });
          }
        }
      }
    }
  };

  // Alias original methods to process additional note tags
  var _DataManager_onLoadEnemy = DataManager.onLoad;
  DataManager.onLoad = function (object) {
    _DataManager_onLoadEnemy.call(this, object);
    if (object === $dataActors) {
      DataManager.processRobotPartsNotetags(object);
    } else if (object === $dataEnemies) {
      DataManager.processRobotPartsNotetags(object);
    }
  };
})();

